import Vue from 'vue'
import Vuex from 'vuex'
import getters from './getters.js'
Vue.use(Vuex)

var store = new Vuex.Store({
	state: {
		cartList: [] //创建一个数组，为了能把数据更好的存放进去
	}, //储存管理的变量。主要是数据相关的
	
	mutations:{
		addCounter(state,payload){
			payload.count++
		},
		addToCart(state,payload){
			payload.checked=true
			state.cartList.push(payload)
		}
	},
	actions: { //因为调用一次只请求一次数据，所以应该去判断
		addCart(context, payload) {
			/* state.cartList.push(payload)  添加数据*/
			let oldProduct = context.state.cartList.find(item => item.iid === payload.iid)
			//查询之前的数组是否有改商品
			//般判断oldProduct
			if (oldProduct) {
				context.commit('addCounter',oldProduct)
			} else {
				payload.count = 1
				context.commit('addToCart', payload)
			}
			/* this.state.cartList.push(payload) */

		}
	}, //修改状态，并是同步的，在组件中使用.$store.commit('',)  
    getters,
   



	/* getters() {

	}, */ //对数据获取之前的再次编译,只要是去计算state的计算属性,$ store.getters.fun()
	/* actions() {

	}, */ //异步操作，在组件中使用是&store.dispath
	/* modules() {

	} */ //store的子模块,为了开发大型项目方便状态管理而使用的.
})
export default store
